package javaproject;
public class CharacterLower {
public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("lowercase Alphabets (a-z):");
System.out.println("..............:");
for (char c='a';c<='z';c++)
{
	System.out.println(c);
	if(c<'z')
	{
		System.out.println("");
	
	}
} }
}