package javaproject;

public class SumArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numbers[] = { 12, 21, 11, 43 };
		int sum = 0;
		for (int number : numbers) {
			sum += number;
		}
			System.out.println("final sum is" + sum);
		}

	}

