package javaproject;

public class Wideningtypecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int i=10;
  long l=i;
  double d=i;
  System.out.println("int i="+i);
  System.out.println("long l="+l);
  System.out.println("double d="+d);
  
	}

}
